    // Load all reviews (no approval needed)
   // Updated loadReviews function
async function loadReviews() {
    const container = document.getElementById('reviewsContainer');
    if (!container) return;
    
    try {
        // Show loading
        container.innerHTML = `
            <div class="col-12 text-center">
                <div class="spinner-border text-warning" role="status">
                    <span class="visually-hidden">Loading reviews...</span>
                </div>
            </div>
        `;
        
        let reviews = [];
        
        // Try to load from Google Sheets first
        try {
            const response = await fetch(GOOGLE_APPS_SCRIPT_URL + '?t=' + Date.now());
            const data = await response.json();
            
            if (data.success && data.reviews && data.reviews.length > 0) {
                reviews = data.reviews;
                console.log('Loaded from Google Sheets:', reviews.length, 'reviews');
            }
        } catch (googleError) {
            console.log('Google Sheets load failed:', googleError);
        }
        
        // Also load from localStorage
        const localReviews = JSON.parse(localStorage.getItem('goldenskyReviews') || '[]');
        
        // Merge reviews (remove duplicates by content)
        const allReviews = [...reviews, ...localReviews];
        
        // Remove duplicates (same name and review text)
        const uniqueReviews = [];
        const seen = new Set();
        
        allReviews.forEach(review => {
            const key = `${review.name}-${review.review.substring(0, 50)}`;
            if (!seen.has(key)) {
                seen.add(key);
                uniqueReviews.push(review);
            }
        });
        
        // Sort by date (newest first)
        uniqueReviews.sort((a, b) => {
            if (a.date === 'Recently') return -1;
            if (b.date === 'Recently') return 1;
            return new Date(b.date) - new Date(a.date);
        });
        
        if (uniqueReviews.length > 0) {
            displayReviews(uniqueReviews.slice(0, 9)); // Show max 9
        } else {
            showEmptyReviewsState();
        }
        
    } catch (error) {
        console.log('Error loading reviews:', error);
        showFallbackReviews();
    }
}
    
